import React from "react";
import "./Contact.css";
import {useForm, ValidationError} from "react";
import {validateEmail} from "../../utils/helpers"
import { Container, Form } from "react-bootstrap";



function Contact () {
    return (
        <div className="contact-wrapper">
          <Container className="contact-container">
            <h1 className="contact-text">Would you like to connect?</h1>
            <Form className="contact-form" onSubmit={onSubmit}>
              <Form.Group controlId="form.Name">
                <Form.Label className="contact-title">
                  Name <BsFillPersonFill />
                </Form.Label>
                <Form.Control
                  className="contact-input"
                  type="text"
                  placeholder="Enter name"
                  onChange={handleInputChange}
                  value={userName}
                  name="userName"
                />
              </Form.Group>
              <Form.Group controlId="form.Email">
                <Form.Label className="contact-title">
                  Email address <BsFillEnvelopeFill />
                </Form.Label>
                <Form.Control
                  className="contact-input"
                  type="email"
                  placeholder="name@example.com"
                  onChange={handleInputChange}
                  value={email}
                  name="email"
                />
              </Form.Group>
              <Form.Group controlId="form.Textarea">
                <Form.Label className="contact-title">
                  Message <BsFillChatDotsFill />
                </Form.Label>
                <Form.Control
                  className="contact-input"
                  as="textarea"
                  rows={3}
                  onChange={handleInputChange}
                  value={text}
                  name="text"
                />
              </Form.Group>
              <Button
                className="contact-submit"
                as="input"
                type="submit"
                value="Submit"
                onClick={handleFormSubmit}
              />
              <h4>{showResults ? <Results /> : null}</h4>
            </Form>

            {errorMessage && (
              <div>
                <p className="error-text">{errorMessage}</p>
              </div>
            )}
          </Container>
        </div>
      );
};

export default Contact;
